import React, {Component} from 'react';
import {Router, Link} from '@reach/router';

import {fetchTopics} from '../utils/api';

class Sidebar extends Component {
	state = {
		topics: [],
	};

	componentDidMount() {
		this.getTopics();
	}
	render() {
		const {topics} = this.state;
		return (
			<table className="topic table">

				{this.props.user.hasOwnProperty('username') ? (
					<th className="welcome-notice">Welcome {this.props.user.username}</th>
				) : (
					<th>Please Login</th>
				)}
				<tr className="table-row">
					<strong>Topics</strong>
				</tr>
				{topics.map((topic) => (
					<tr className="table-row" key={topic.slug}>
						<Link to={`/topics/${topic.slug}/articles`}>
							<td>{topic.slug}</td>
						</Link>
					</tr>
				))}
				<tr className="table-row">
					<strong>Sort Articles</strong>
				</tr>
				<tr className="table-row">
					<Link to="/articles">Sort By Date</Link>
				</tr>
				<tr className="table-row">
					<Link to="/articles">Sort By Comment Count</Link>
				</tr>
				<tr className="table-row">
					<Link to="/articles">Sort By Votes</Link>
				</tr>
			</table>
		);
	}
	getTopics = () => {
		fetchTopics().then(({data}) => {
			this.setState({topics: data.topics});
		});
	};
}

export default Sidebar;
