import React, {Component} from 'react';
import {Router, Link} from '@reach/router';

import {fetchTopics} from '../utils/api';

class Sidebar extends Component {
	state = {
		topics: [],
	};

	componentDidMount() {
		this.getTopics();
	}
	render() {
		const {topics} = this.state;
		return (
			<div className="Sidebar">
				<table className="topic table">
					{this.props.user.hasOwnProperty('username') ? (
						<tbody className="welcome-notice">
							Welcome {this.props.user.username}
						</tbody>
					) : (
						<tbody>
							<tr className="welcome-notice">Please Login</tr>
						</tbody>
					)}

					<tbody>
						<tr className="table-row">
							<strong>Topics</strong>
						</tr>
						{topics.map((topic) => (
							<tr className="table-row" key={topic.slug}>
								<td>
									<Link to={`/topics/${topic.slug}/articles`}>
										{topic.slug}
									</Link>
								</td>
							</tr>
						))}
					</tbody>
				</table>
				<table className="topic table">
					<tbody>
						<tr className="table-row-header">Sort Articles</tr>
						<tr className="table-row">
							<Link to="/articles/?sort_by">Sort By Date</Link>
						</tr>
						<tr className="table-row">
							<Link to="/articles/?sort_by">Sort By Comment Count</Link>
						</tr>
						<tr className="table-row">
							<Link to="/articles/?sort_by=votes">Sort By Votes</Link>
						</tr>
					</tbody>
				</table>
			</div>
		);
	}
	getTopics = () => {
		fetchTopics().then(({data}) => {
			this.setState({topics: data.topics});
		});
	};
}

export default Sidebar;
