import React, {Component} from 'react';
import {Router, Link} from '@reach/router';
import {
	fetchAllArticles,
	fetchArticlesByTopic,
	fetchArticlesByUser,
} from '../utils/api.js';

class Articles extends Component {
	state = {
		articles: [],
		topic: '',
		user: '',
	};

	componentDidMount() {
		console.log('mounted');
		if (this.props.user) {
			console.log(this.props.user);
			return this.getArticlesByUser(this.props.user);
		}
		return this.props.topic
			? this.getArticlesByTopic(this.props.uri)
			: this.getAllArticles();
	}

	componentDidUpdate(prevProps, prevState) {
		if (prevProps.user !== this.props.user) {
			return this.getArticlesByUser(this.props.user);
		}
		if (prevProps.topic !== this.props.topic) {
			return !this.props.topic
				? this.getArticlesByTopic(this.props.uri)
				: this.getAllArticles();
		}
	}

	render() {
		return (
			<div className="Main">
				<h1 className="Header">Articles</h1>
				<ul>
					{this.state.articles.map((article) => {
						return (
							<p key={article.article_id} className="individual-item">
								<Link to={`/article/${article.article_id}`}>
									{article.title}
								</Link>
							</p>
						);
					})}
				</ul>
			</div>
		);
	}
	getAllArticles = () => {
		return fetchAllArticles().then(({data}) => {
			this.setState({articles: data.articles});
		});
	};

	getArticlesByTopic = (topicURI) => {
		return fetchArticlesByTopic(topicURI).then(({data}) => {
			this.setState({articles: data.articles});
		});
	};
	getArticlesByUser = (username) => {
		console.log('by user called');
		return fetchArticlesByUser(username).then(({data}) => {
			console.log('dattaaaa', data);
			this.setState({articles: data.articles});
		});
	};
}

export default Articles;
