import React, {Component} from 'react';
import {voteOnArticle} from '../utils/api';

class Voter extends Component {
	state = {
		newVote: 0,
	};

	render() {
		const {votes} = this.props;
		const {newVote} = this.state;

		return (
			<div>
				<button disable={newVote === 1} onClick={() => this.handleVote(1)}>
					Vote Up
				</button>
				<span>Votes : {votes + newVote}</span>
				<button disable={newVote === -1} onClick={() => this.handleVote(-1)}>
					Vote Down
				</button>
			</div>
		);
	}
	handleVote = (increment) => {
		const {articleID} = this.props;
		voteOnArticle(increment, articleID).then(({data}) => {
			console.log(data.votedArticle.votes);
			this.setState((state, props) => {
				return {counter: state.newVote + props.step};
			});
			// this.setState({newVote: state.newVote + increment});
		});
	};
}

export default Voter;
