import React, {Component} from 'react';
import {fetchCommentsForArticle, postCommentOnArticle} from '../utils/api';

class Comments extends Component {
	state = {
		comments: [],
		addedComment: '',
	};
	componentDidMount() {
		return this.getCommentsForArticle(this.props.id);
	}

	render() {
		const {comments} = this.state;

		return (
			<div>
				<form action="">
					<textarea
					rw
						className="comment-box"
						type="text"
						onChange={this.handleCommentChange}
					/>
					<button onClick={this.handleCommentSubmit}>Post Comment</button>
				</form>
				<ul className="Comments-container">
					{comments.map((comment) => {
						const {votes, created_at, author, comment_id, body} = comment;
						return (
							<li key={comment_id} className="Comments-item">
								<p className="body"> {body}</p>
								<span>{author}</span> {' | '} <span>{votes}</span>
								<p>{created_at}</p>
							</li>
						);
					})}
				</ul>
			</div>
		);
	}
	getCommentsForArticle = (articleID) => {
		fetchCommentsForArticle(articleID).then(({data}) => {
			this.setState({comments: data.comments});
		});
	};
	handleCommentChange = (event) => {
		return this.setState({addedComment: event.target.value});
	};
	handleCommentSubmit = (event) => {
		event.preventDefault();
		const {addedComment} = this.state;
		const {id, user} = this.props;
		console.log(id, user.username, addedComment);
		postCommentOnArticle(id, user.username, addedComment).then((data) => {
			console.log(data);
		});
	};
}

//spread operator in state

export default Comments;
