import React, {Component} from 'react';
import {fetchCommentsForArticle} from '../utils/api';

class Comments extends Component {
	state = {
		comments: [],
	};
	componentDidMount() {
		return this.getCommentsForArticle(this.props.id);
	}

	render() {
		const {comments} = this.state.comments;
		console.log(comments);
		if (comments)
			return (
				<div>
					{
						<ul className="Comments-container">
							{comments.map((comment) => {
								console.log(comment);
								return (
									<p key={comment.comment_id} className="Comments-item">
										{comment.body}
									</p>
								);
							})}
						</ul>
					}
				</div>
			);
	}
	getCommentsForArticle(articleID) {
		fetchCommentsForArticle(articleID).then(({data}) => {
			this.setState({comments: data.comments});
		});
	}
}

export default Comments;
