import React, {Component} from 'react';
import {Router, Link} from '@reach/router';
import {fetchAllArticles} from '../utils/api.js';
import {fetchArticlesByTopic} from '../utils/api';
import Topics from './Topics.jsx';

class Articles extends Component {
	state = {
		articles: [],
		topic: '',
		user: '',
	};

	componentDidMount() {
		console.log(this.props.user);
		if(this.props.uri === '/articles') return 
		if (this.props.uri.length !== '/articles')
			return this.getArticlesByTopic(this.props.uri);
		else return this.getAllArticles();
	}

	componentDidUpdate(prevProps, prevState) {
		if (prevProps.topic !== this.props.topic) {
			return this.props.topic
				? this.getArticlesByTopic(this.props.uri)
				: this.getAllArticles();
		}
	}

	render() {
		return (
			<div className="Main">
				<h1 className="Header">Articles</h1>
				<ul>
					{this.state.articles.map((article) => {
						return (
							<p key={article.article_id} className="individual-item">
								<Link to={`/article/${article.article_id}`}>
									{article.title}
								</Link>
							</p>
						);
					})}
				</ul>
			</div>
		);
	}
	getAllArticles() {
		return fetchAllArticles().then(({data}) => {
			console.log('all articles called');
			this.setState({articles: data.articles});
		});
	}

	getArticlesByTopic(topicURI) {
		console.log('by topic called');
		return fetchArticlesByTopic(topicURI).then(({data}) => {
			this.setState({articles: data.articles});
		});
	}
}

export default Articles;
