import React, {Component} from 'react';
import {Router, Link} from '@reach/router';
import {fetchIndividualArticle, fetchCommentsForArticle} from '../utils/api';
import '../App.css';

class IndividualArticle extends Component {
	state = {
		article: {},
		comments: [],
	};
	componentDidMount() {
		return (
			this.getIndividualArticles(this.props.id),
			this.getCommentsForArticle(this.props.id)
		);
	}

	render() {
		const {
			topic,
			votes,
			title,
			body,
			author,
			comment_count,
			created_at,
		} = this.state.article;
		const {comments} = this.state;
		return (
			<div>
				<h1 className="Header">{title}</h1>
				<p className="Article">{body}</p>
				<p>votes: {votes}</p>
				<span>
					<Link to={`/topics/${topic}/articles`}>{topic}</Link>
				</span>
				{' | '}
				<span>{author}</span>
				<p>{comment_count}</p>
				<span>{created_at}</span>
				<ul>
					{comments.map((comment) => {
						return (
							<div className="Comments-container">
								<p>{comment.body}</p>
							</div>
						);
					})}
				</ul>
			</div>
		);
	}
	getIndividualArticles(articleId) {
		return fetchIndividualArticle(articleId).then(({data}) => {
			this.setState({article: data.article});
		});
	}
	getCommentsForArticle(articleID) {
		fetchCommentsForArticle(articleID).then(({data}) => {
			this.setState({comments: data.comments});
		});
	}
}

export default IndividualArticle;
