import axios from 'axios';
const BASE_URL = 'https://the-nc-news.herokuapp.com';

const fetchAllArticles = () => {
	return axios.get(`${BASE_URL}/api/articles`).then((data) => console.log(data));
};

export default fetchAllArticles
