import React, {Component} from 'react';
import {Router, Link} from '@reach/router';

import {fetchTopics} from '../utils/api';

class Sidebar extends Component {
	state = {
		topics: [],
	};

	componentDidMount() {
		this.getTopics();
	}
	render() {
		const {topics} = this.state;
		console.log(this.props);
		return (
			<div className="Sidebar">
				<p className="topic table" />
				{this.props.user.hasOwnProperty('username') ? (
					<p className="welcome-notice">
						<p>Welcome {this.props.user.username}</p>
						<button onClick={this.props.logout}>Logout</button>
					</p>
				) : (
					<p>
						<tr className="welcome-notice">Please Login</tr>
					</p>
				)}
				<p className="table-row" />><strong>Topics</strong>
				{topics.map((topic) => (
					<p className="table-row" key={topic.slug}>
						<Link to={`/topics/${topic.slug}/articles`}>{topic.slug}</Link>
					</p>
				))}
			</div>
		);
	}
	getTopics = () => {
		fetchTopics().then(({data}) => {
			this.setState({topics: data.topics});
		});
	};
	// handleLogout = (event) => {
	// 	return this.props.logout();
	// };
}

export default Sidebar;
